import React, { useState } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';

function AdminLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const [errors, setErrors] = useState([]);

  const loginHandler = async () => {
    try {
      const response = await axios.post('http://localhost:8000/api/loginAdmin', {
        email: email,
        password: password
      });
      navigate('/dashboardAdmin');
      console.log(response.data);
      // Handle hasil login (misalnya, penyimpanan token di session)
    } catch (error) {
      console.error('Login failed: ', error);
    }
  }

    // const [email, setEmail] = useState("");
    // const [password, setPassword] = useState("");

    // //define state validation
    // const [errors, setErrors] = useState([]);

    // //define history
    // const navigate = useNavigate();

    // //function "loginHanlder"
    // const loginHandler = async (e) => {
    //     e.preventDefault();

    //     //initialize formData
    //     const formData = new FormData();

    //     //append data to formData
    //     formData.append('email', email);
    //     formData.append('password', password);

    //     //send data to server
    //     await axios.post('http://localhost:8000/api/loginAdmin', formData)
    //     .then((response) => {

    //         //redirect to dashboard
    //         navigate('/dashboardAdmin');
    //     })
    //     .catch((errors) => {

    //         //assign error to state "validation"
    //         setErrors(errors.response.data);
    //     })
    // };


  return (
    // <div>
    //     <h2>Admin Login</h2>
    //     <input type="text" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} />
    //     <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} />
    //     <button onClick={handleLogin}>Login</button>
    // </div>

    <div className="container" style={{ marginTop: "120px" }}>
      <div className="row justify-content-center">
        <div className="col-md-4">
          <div className="card border-0 rounded shadow-sm">
            <div className="card-body">
              <h4 className="fw-bold">HALAMAN LOGIN</h4>
              <hr />
              {
                errors.message && (
                  <div className="alert alert-danger">
                    {errors.message}
                  </div>
                )
              }
              <form onSubmit={loginHandler}>
                <div className="mb-3">
                  <label className="form-label">ALAMAT EMAIL</label>
                  <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Masukkan Alamat Email" />
                </div>
                {
                  errors.email && (
                    <div className="alert alert-danger">
                      {errors.email[0]}
                    </div>
                  )
                }
                <div className="mb-3">
                  <label className="form-label">PASSWORD</label>
                  <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Masukkan Password" />
                </div>
                {
                  errors.password && (
                    <div className="alert alert-danger">
                      {errors.password[0]}
                    </div>
                  )
                }
                <div className="d-grid gap-2">
                  <button type="submit" className="btn btn-primary">LOGIN</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

