//import hook react
import React, { useState, useEffect } from 'react';

//import hook useHitory from react router dom
import { useNavigate } from 'react-router-dom';

//import axios
import axios from 'axios';

function Akun() {

    //state user
    const [user, setUser] = useState({});

    //define history
    const navigate = useNavigate();

    //token
    const token = localStorage.getItem("token");

    //function "fetchData"
    const fetchData = async () => {

        //set axios header dengan type Authorization + Bearer token
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`
        //fetch user from Rest API
        await axios.get('http://localhost:8000/api/user')
            .then((response) => {

                //set response user to state
                setUser(response.data);
            })
    }

    //hook useEffect
    useEffect(() => {

        //check token empty
        if (!token) {

            //redirect login page
            navigate('/login');
        }

        //call function "fetchData"
        fetchData();
    }, []);

    //function logout
    const logoutHanlder = async () => {

        //set axios header dengan type Authorization + Bearer token
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`
        //fetch Rest API
        await axios.post('http://localhost:8000/api/logout')
            .then(() => {

                //remove token from localStorage
                localStorage.removeItem("token");

                //redirect halaman login
                navigate('/login');
            });
    };

    return (
        <>
            <Navbar
                sticky="top"
                expand="lg"
                className="bg-success border-bottom"
                style={{ height: "85px", fontSize: "20px" }}
            >
                <Container>

                    <img
                        src={foodrecipes}
                        alt="rectangle"
                        className={styles['font1']}
                    />
                    <img
                        src={sendok}
                        alt="rectangle"
                        className={styles['sendok']}
                    />
                    <div className={styles['search2']}>
                        <div className="flex items-center">
                            <input
                                type="text"
                                className="border rounded p-1 w-1/7"
                                placeholder="Cari..."
                                value={query}
                                onChange={(e) => setQuery(e.target.value)}
                            />

                            {/* <button onClick={handleSearch} className="bg-blue-500 text-white rounded p-1 ml-2" type="button">
                Cari
              </button> */}

                            <button className="bg-blue-500 text-white rounded p-1 ml-2" onClick={handleSearch}>Cari</button>
                        </div>
                    </div>
                </Container>
            </Navbar>
            <div className="container" style={{ marginTop: "50px" }}>
                <div className="row justify-content-center">
                    <div className="col-md-12">
                        <div className="card border-0 rounded shadow-sm">
                            <div className="card-body">
                                SELAMAT DATANG <strong className="text-uppercase">{user.name}</strong>
                                <hr />
                                <button onClick={logoutHanlder} className="btn btn-md btn-danger">LOGOUT</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    )

}

export default Akun;
