import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

import styles from "../c_iphone-14-3.module.css";
import logo from "../assets3/logo.png";
import "bootstrap/dist/css/bootstrap.min.css";
import { Link } from "react-router-dom";
import '../c_iphone-14-5.module.css';

export const NewRecipe = () => {
  // const { formValues, onChange, storeResep, errors, setErrors } =
  //   useContext(ResepContext);
  // useEffect(() => {
  //   setErrors({});

  const [image, setImage] = useState('');
  const [title, setTitle] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [step, setStep] = useState('');
  const [namaakun, setNamaAkun] = useState('');

  const [errors, setErrors] = useState([]);

  const navigate = useNavigate();

  const handleFileChange = (e) => {
    setImage(e.target.files[0]);
  }

  const storeResep = async (e) => {
    e.preventDefault();

    const formData = new FormData();

    formData.append('image', image);
    formData.append('title', title);
    formData.append('ingredients', ingredients);
    formData.append('step', step);
    formData.append('namaakun', namaakun);

    await axios.post("reseps", formData)
      .then(() => {

        navigate('/');


      })
      .catch(error => {

        setErrors(error.response.data);
      })
  }


  return (
    // <div className="bg-slate-200 w-full h-full">
    //   <div className="max-w-7xl mx-auto min-h-screen">
    //     <form onSubmit={storeResep}
    //       className="max-w-md mx-auto p-4 bg-white shadow-md rounded-sm pt-60"
    //     >
    //       <div className="space-y-6">
    //         <div className="mb-4">
    //           <label className="block mb-2 text-sm font-medium">
    //             Nama Resep
    //             <input
    //               name="title"
    //               value={formValues["title"]}
    //               onChange={onChange}
    //               className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
    //             />
    //           </label>

    //         </div>
    //         <div className="mb-4">
    //           <label className="block mb-2 text-sm font-medium">
    //             Gambar
    //             <input
    //               type='file'
    //               name="image"
    //               value={formValues["image"]}
    //               onChange={onChange}
    //             />
    //           </label>

    //         </div>
    //         <div className="mb-4">
    //           <label className="block mb-2 text-sm font-medium">
    //             Bahan dan Alat
    //             <input
    //               name="ingredients"
    //               value={formValues["ingredients"]}
    //               onChange={onChange}
    //               className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
    //             />
    //           </label>


    //         </div>
    //         <div className="mb-4">
    //           <label className="block mb-2 text-sm font-medium">
    //             Langkah-Langkah
    //             <input
    //               name="step"
    //               value={formValues["step"]}
    //               onChange={onChange}
    //               className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
    //             />
    //           </label>



    //         </div>
    //         <div className="mb-4">
    //           <label className="block mb-2 text-sm font-medium">
    //             Nama Akun
    //             <input
    //               name="namaakun"
    //               value={formValues["namaakun"]}
    //               onChange={onChange}
    //               className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
    //             />
    //           </label>


    //         </div>
    //       </div>
    //       <div className="my-4">
    //         <button className="px-4 py-2 bg-indigo-500 hover:bg-indigo-700 text-white rounded-md">
    //           ADD
    //         </button>
    //       </div>
    //     </form>
    //   </div>
    // </div>

    <div className="mt-12">
      <form
        encType="multipart/form-data"
        onSubmit={storeResep}

        className="max-w-md mx-auto p-4 bg-white shadow-md rounded-sm"
      >
        <div className="space-y-6">
          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium">
              Nama Resep
            </label>
            <input
              name="title"
              onChange={(e) => setTitle(e.target.value)}
              className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
            />
            {errors.title && (
              <span className="text-sm text-red-400">{errors.title[0]}</span>
            )}
          </div>
          {/* <div className="mb-4">
          <label htmlFor="slug" className="block mb-2 text-sm font-medium">
            Slug
          </label>
          <input
          type="file"
            name="image"
            value={formValues.image}
            onChange={onChange}
            className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
          />
          {errors.image && (
            <span className="text-sm text-red-400">{errors.image[0]}</span>
          )}
        </div> */}
          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium">
              Image
            </label>
            <input
            type='file'
              name="ingredients"
              onChange={handleFileChange}
              className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
            />
            {errors.ingredients && (
              <span className="text-sm text-red-400">{errors.ingredients[0]}</span>
            )}
          </div>
          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium">
              Ingredients
            </label>
            <input
              name="ingredients"
              // value={formValues.ingredients}
              onChange={(e) => setIngredients(e.target.value)}
              className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
            />
            {errors.ingredients && (
              <span className="text-sm text-red-400">{errors.ingredients[0]}</span>
            )}
          </div>
          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium">
              Step
            </label>
            <input
              name="step"
              // value={formValues.step}
              onChange={(e) => setStep(e.target.value)}
              className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
            />
            {errors.step && (
              <span className="text-sm text-red-400">{errors.step[0]}</span>
            )}
          </div>
          <div className="mb-4">
            <label htmlFor="name" className="block mb-2 text-sm font-medium">
              Nama Akun
            </label>
            <input
              name="namaakun"
              // value={formValues.namaakun}
              onChange={(e) => setNamaAkun(e.target.value)}
              className="border border-gray-300 text-gray-900 text-sm rounded-md block w-full p-2"
            />
            {errors.namaakun && (
              <span className="text-sm text-red-400">{errors.namaakun[0]}</span>
            )}
          </div>
        </div>
        <div className="my-4">
          <button className="px-4 py-2 bg-indigo-500 hover:bg-indigo-700 text-white rounded-md">
            Store
          </button>
        </div>
      </form>
    </div>
  );
};
export default NewRecipe
