//import hook react
import React, { useState } from 'react';
import { useEffect } from 'react';

//import hook useHitory from react router dom
import { useHistory, useNavigate } from 'react-router';
import { Link } from "react-router-dom";

//import axios
import axios from 'axios';
import ReCAPTCHA from "react-google-recaptcha";

function Login() {

    //define state
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    //define state validation
    const [errors, setErrors] = useState([]);
    const navigate = useNavigate();

    const [IsRecaptchaVerified, SetChaptchaVerified] = useState(false);

    //function "loginHanlder"
    const loginHandler = async (e) => {
        e.preventDefault();

        if (!IsRecaptchaVerified) {
            alert ("Tekan CAPTCHA!");
            return;
        }else {
            //initialize formData
        const formData = new FormData();

        //append data to formData
        formData.append('email', email);
        formData.append('password', password);

        //send data to server
        await axios.post('http://localhost:8000/api/login', formData)
        .then((response) => {

            //set token on localStorage
            localStorage.setItem('token', response.data.token);
            localStorage.setItem('roles', response.data.role);

            //redirect to dashboard
            navigate('/akun');
        })
        .catch((error) => {

            //assign error to state "validation"
            setErrors(error.response.data);
        })
        }
    };

    return (
        <div className="container" style={{ marginTop: "120px" }}>
            <div className="row justify-content-center">
                <div className="col-md-4">
                    <div className="card border-0 rounded shadow-sm">
                        <div className="card-body">
                            <h4 className="fw-bold">HALAMAN LOGIN</h4>
                            <hr/>
                            {
                                errors.message && (
                                    <div className="alert alert-danger">
                                        {errors.message}
                                    </div>
                                )
                            }
                            <form onSubmit={loginHandler}>
                                <div className="mb-3">
                                    <label className="form-label">ALAMAT EMAIL</label>
                                    <input type="email" className="form-control" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Masukkan Alamat Email"/>
                                </div>
                                {
                                    errors.email && (
                                        <div className="alert alert-danger">
                                            {errors.email[0]}
                                        </div>
                                    )
                                }
                                <div className="mb-3">
                                    <label className="form-label">PASSWORD</label>
                                    <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Masukkan Password"/>
                                </div>
                                {
                                    errors.password && (
                                        <div className="alert alert-danger">
                                            {errors.password[0]}
                                        </div>
                                    )
                                }
                                <ReCAPTCHA sitekey="6LdSeuooAAAAAAV672GzCZWUMt9l8bcB7GyYfdcf" onChange={() => SetChaptchaVerified(true)} /> 
                                <br />
                                <div className="d-grid gap-2">
                                    <button type="submit" className="btn btn-primary">LOGIN</button>
                                </div>
                                <div>
                                    <h8>Belum punya akun?</h8>
                                    <Link to="/register">
                                    <h8>Daftar di sini </h8></Link>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default Login;
