  const [admin, setAdmin] = useState(null);

  useEffect(() => {
    // Ambil data pengguna saat komponen dimuat
    axios
      .get('http://localhost:8000/api/admin')
      .then((response) => {
        setUser(response.data);
      })
      .catch((error) => {
        console.error('Failed to get user data: ', error);
      });
  }, []);

