                    </div>

                    <div className={styles['search2']}>
                        <div className="flex items-center">
                            <input
                                type="text"
                                className="border rounded p-1 w-1/7"
                                placeholder="Cari..."
                            />
                            <button className="bg-blue-500 text-white rounded p-1 ml-2" type="button">
                                Cari
                            </button>
                        </div>
                    </div>

                    <div className="container mt-5">
      <Carousel touch={true} slide={true} interval={10000}>
        <Carousel.Item className='text-center shadow mb-5 bg-body-tertiary rounded'>
          <Image src="https://linebank.co.id/blog/wp-content/uploads/2023/04/Screenshot_2.png" alt="" style={CarouselImage} fluid rounded />
            {/* <Carousel.Caption>
              <h3>First slide label</h3>
              <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption> */}
         </Carousel.Item>
         <Carousel.Item className='text-center shadow mb-5 bg-body-tertiary rounded'>
         <Image src="https://linebank.co.id/blog/wp-content/uploads/2023/04/Screenshot_2.png" alt="" style={CarouselImage} fluid rounded />
            {/* <Carousel.Caption>
              <h3>Second slide label</h3>
              <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
            </Carousel.Caption> */}
          </Carousel.Item>
          <Carousel.Item className='text-center shadow mb-5 bg-body-tertiary rounded'>
