            {/* <Carousel.Caption>
              <h3>Third slide label</h3>
              <p>
                Praesent commodo cursus magna, vel scelerisque nisl consectetur.
              </p>
            </Carousel.Caption> */}
          </Carousel.Item>
        </Carousel>
    </div>
    

                    <div className={styles['kertas01']}>
                        <img
                            src={rectangle1}
                            alt="rectangle"
                            className='w-full h-24 -bottom-15 absolute inset-x-0'
                        />
                    </div>
                    <div className='relative inset-x-0 bottom-7'>
                        <Link to="/homepage">
                            <img
                                src={rumah}
                                alt="rectangle"
                                className={styles['rumah']}
                            /></Link>
                        <Link to="/save">
                            <img
                                src={save}
                                alt="rectangle"
                                className={styles['save']}
                            /></Link>
                        <Link to="/user">
                            <img
                                src={akun}
                                alt="rectangle"
                                className={styles['akun']}
                            /></Link>
                    </div>
                </div>
                {/* <div className='container mt-0'>
                <div className={styles['card']}>
                <div className="card" style={{ width: '11rem'}}>
                            <img src={bg} className="card-img-top" alt="..." />
                            <div className="card-body">
